sysauto_script.script:
var queue = new GlideRecord('x_snc_demohub_app_install_queue');
queue.addEncodedQuery('parent=NULL^state=Queued');
queue.query();



var gr = new GlideRecord('x_snc_demohub_app_configuration');
gr.addEncodedQuery('state!=Installed');
gr.query();
gr.deleteMultiple();
var gr1 = new GlideRecord('x_snc_demohub_app_narrative');
gr1.query();
gr1.deleteMultiple();
new x_snc_demohub_app.server().updateWillInstallConfigurationSysID();
new x_snc_demohub_app.server().POSTCheckin();
new x_snc_demohub_app.server().GETDemoHubApp();
new x_snc_demohub_app.server().GETConfigurationList();
new x_snc_demohub_app.server().GETNarrativeList();
new x_snc_demohub_app.install().checkNode();
new x_snc_demohub_app.server().GETConfigurationList();
new x_snc_demohub_app.server().GETNarrativeList();
new x_snc_demohub_app.server().updateWillInstallConfiguration();

while (queue.next()){

	new x_snc_demohub_app.installQueue().addConfiguration(queue.sys_id,'',queue.title);
	queue.state='Error';
	queue.update();

}
